mapper.select:
SELECT 

FROM cabinetms_unit a

WHERE a.id = #{id}

SELECT
FROM cabinetms_unit a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.no LIKE '%'||#{no}||'%' '%'+#{no}+'%' concat('%',#{no},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.type = #{type}
AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM cabinetms_unit a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cabinetms_unit SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
